import React, { useState } from 'react';

var d = new Date();

function Wave(){
    const[swell,setSwell] = useState({});


    const search = evt =>{
        if (evt.key === "Enter"){
        fetch(`https://api.stormglass.io/v2/weather/point?lat=21.275767&lng=-157.827374&params=swellHeight,swellDirection,swellPeriod&key=947985f0-4e3b-11eb-a422-0242ac130002-947986ea-4e3b-11eb-a422-0242ac130002`, 
        ).then((response) => response.json())
          .then((jsonData) => {
            console.log(jsonData);
            setSwell(jsonData);

          });
    }
}
    return (
        <div className="Weather">
            <main>
            <div className="search-box">
    {/*
            <input 
            type="text"
            className="search-bar-lat"
            placeholder="Enter Latitude:"
            onChange={e => setLat(e.target.value)}
            value={lat}
            onKeyPress={search}
          /> 
    */}
          <input 
            type="text"
            className="search-bar-lng"
            placeholder="Enter Longitude:"
            //onChange={e => setLng(e.target.value)}
            //value={lng}
            onKeyPress={search}
          /> 
        </div>
            {(typeof swell.data != "undefined") ? (
                <div>
                <div className="wave-box">
                    <div className="info">

                        {swell.hours[d.getHours()].swellDirection.noaa} at {swell.hours[d.getHours()].swellHeight.noaa}

                        THIS IS A TEST

                    </div>


                </div>
                </div>
                ): (' ')}
            </main>
        </div>
    )
}
export default Wave;
