import React, { useState }from 'react';
import Wave from './Wave';
{/*
const api = {
  key: "1a94c0e7608e7e5cf865f27d79784c58",
  base: "https://api.openweathermap.org/data/2.5/"
}
*/}

function App() {


  const[query, setQuery] = useState('');
  const[weather, setWeather] = useState({});

  const search = evt =>{
    if (evt.key === "Enter"){
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
      .then(res => res.json())
      .then(result => {
        setWeather(result);
        setQuery('');
        console.log(result);

    });
  }
}

  const[swell,setSwell] = useState({});
    const[lng, setLng] = useState();
    const[lat, setLat] = useState();
    const params = 'swellHeight,swellDirection,swellPeriod';

  const search1 = evt =>{
    if (evt.key === "Enter"){
    fetch(`https://api.stormglass.io/v2/weather/point?lat=${lat}&lng=${lng}&params=${params}`, {
        headers: {
          'Authorization': '947985f0-4e3b-11eb-a422-0242ac130002-947986ea-4e3b-11eb-a422-0242ac130002'
        }
      }).then((response) => response.json()).then((jsonData) => {
        console.log(jsonData);
        setSwell(jsonData);
        setLng();
        setLat();
      });
  }
  }


  const dateBuilder = (d) =>{
    let months = ["January","February","March","April","May","June","July",
    "August","September","October","November","December"];
    let days = ["Sunday","Monday","Tuesday","Wednesday",
  "Thursday","Friday","Saturday"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`
  }





  return (
    <div className="app">
      <main>
        <div className="search-box">
          <input 
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
            />
        </div>
        {(typeof weather.main != "undefined") ? (
        <div>
        <div className="location-box">
              <div className="location">{weather.name},{weather.sys.country}</div>
              <div className="date">{dateBuilder(new Date())}</div>

            </div>
            <div className="weather-box">
              <div className="temp">
                {Math.round(weather.main.temp)}˚C
              </div>
              <div className="weather">{weather.weather[0].main}</div>
            </div>
          </div>
          ): (' ')}

          <Wave />




      </main>
    </div>
        );

}

export default App;
