import React, { useState }from 'react';

function Wave(){
    const[swell,setSwell] = useState({});
    const[lng, setLng] = useState();
    const[lat, setLat] = useState();
    const params = 'swellHeight,swellDirection,swellPeriod';

    const search = evt =>{
        if (evt.key === "Enter"){
        fetch(`https://api.stormglass.io/v2/weather/point?lat=${lat}&lng=${lng}&params=${params}`, {
            headers: {
              'Authorization': '947985f0-4e3b-11eb-a422-0242ac130002-947986ea-4e3b-11eb-a422-0242ac130002'
            }
          }).then((response) => response.json()).then((jsonData) => {
            console.log(jsonData);
            setSwell(jsonData);
            setLng(); 
            setLat();
          });
    }
}
    return (
        <div className="Weather">
            <main>
            <div className="search-box">
          <input 
            type="text"
            className="search-bar-lng"
            placeholder="Enter Longitude:"
            onChange={e => setLng(e.target.value)}
            value={lng}
            onKeyPress={search}
          /> 
          <input 
            type="text"
            className="search-bar-lat"
            placeholder="Enter Latitude:"
            onChange={e => setLat(e.target.value)}
            value={lat}
            onKeyPress={search}
          /> 
        </div>
            {(typeof swell.data != "undefined") ? (
                <div>
                <div className="wave-box">
                    <div className="info">
                        {swell.hours[0].swellDirection.noaa} at {swell.hours[0].swellHeight.noaa}
                        Test
                    </div>


                </div>
                </div>
                ): (' ')}
            </main>
        </div>
    )
}
export default Wave;
